btn.style.cursor="pointer",user_name.style.cursor="text",userpassword.style.cursor="text";let usern,passw;btn.addEventListener("click",vorbereiten);function vorbereiten(){usern=document.getElementById("user_name").value,passw=document.getElementById("userpassword").value,usern.length>0&&passw.length>0&&anfrage_abschicken(usern,passw)}async function anfrage_abschicken(e,n){alert("username: "+e+", password: "+n),document.getElementById("user_name").value="",document.getElementById("userpassword").value="",document.getElementById("user_name").className="user_icon_black",document.getElementById("userpassword").className="password_icon_black"}document.addEventListener("keydown",function(e){if(e.key==="Enter")if(document.getElementById("user_name").value.length!==0&&document.getElementById("userpassword").value.length!==0)vorbereiten();else return e.preventDefault(),!1});function user_name_event(e){user_input=e.value.length,user_input===0?document.getElementById("user_name").className="user_icon_black":document.getElementById("user_name").className="user_icon_orange"}function password_event(e){password_input=e.value.length,password_input===0?document.getElementById("userpassword").className="password_icon_black":document.getElementById("userpassword").className="password_icon_orange"}
